import React from "react";
import StripeCheckout from "react-stripe-checkout";
import axios from "axios";

const StripeCheckoutButton = ({ price }) => {
  const priceForStripe = price * 100;
  const publishableKey =
    "pk_test_51J8LhPHicN8yYPw6H9GQueEiqNcSP4fUj0VR4nYEIZP7nJCFFBkmbot2gnEPxkIERvzurF9lsQZpY4JJATNUp0Lm00XroieV73";

  const onToken = (token) => {
    axios({
      url: "payment",
      method: "post",
      data: {
        amount: priceForStripe,
        token,
      },
    })
      .then((response) => {
        alert("Pembayaran Berhasil!");
      })
      .catch((error) => {
        console.log("Pembayaran gagal: ", JSON.parse(error));
        alert(
          "Pembayaran tidak berhasil. Silahkan gunakan kartu kredit untuk testing yang diberikan!"
        );
      });
  };
  return (
    <StripeCheckout
      label="Pay Now"
      name="Khas Nusantara"
      billingAddress
      shippingAddress
      image="https://svgshare.com/i/CUz.svg"
      description={`Your total is $${price}`}
      amount={priceForStripe}
      panelLabel="Pay Now"
      token={onToken}
      stripeKey={publishableKey}
    />
  );
};

export default StripeCheckoutButton;
