import React, { useEffect } from "react";
import { Route, Switch, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";

import { selectCurrentUser } from "./redux/user/user.selectors";

import "./App.css";
import HomePage from "./pages/homepage/homepage.component";
import ShopPage from "./pages/shop/shop.component";
import CheckoutPage from "./pages/checkout/checkout.component";
import Header from "./components/header/header.component";
import SignInAndSignUp from "./pages/sign-in-and-sign-up/sign-in-and-sign-up.component";
import { checkUserSession } from "./redux/user/user.actions";
import Toaster from "./components/toaster/toaster.component";
import DashboardPage from "./pages/dashboard/dashboard.component";

import ProfilePage from "./pages/dashboard/profile/profile.component";
import ReviewsPage from "./pages/dashboard/reviews/reviews.component";

const App = ({ checkUserSession, currentUser }) => {
  console.log("CURRENT USER", currentUser);
  useEffect(() => {
    checkUserSession();
  }, [checkUserSession]);

  return (
    <div>
      <Header />
      <Toaster />
      <Switch>
        <Route exact path="/" component={HomePage} />
        <Route path="/shop" component={ShopPage} />
        <Route exact path="/checkout" component={CheckoutPage} />
        <Route
          exact
          path="/dashboard"
          component={() =>
            currentUser ? <DashboardPage /> : <Redirect to="/signin" />
          }
        />
        <Route
          exact
          path="/dashboard/profile"
          component={() =>
            currentUser ? (<ProfilePage />) : <Redirect to="/dashboard" />
          }
        />
        <Route
          exact
          path="/dashboard/reviews"
          component={() =>
            currentUser && currentUser.role === 1 ? <ReviewsPage /> : <Redirect to="/dashboard" />
          }
        />
        <Route
          exact
          path="/signin"
          render={() =>
            currentUser ? <Redirect to="/dashboard" /> : <SignInAndSignUp />
          }
        />
      </Switch>
    </div>
  );
};

const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
});

const mapDispatchToProps = (dispatch) => ({
  checkUserSession: () => dispatch(checkUserSession()),
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
