import { takeEvery, call, put } from "@redux-saga/core/effects";
import {
  firestore,
  convertCollectionsSnapshotToMap,
} from "../../firebase/firebase.utils";

import ShopActionTypes from "./shop.types";
import {
  fetchCollectionsSuccess,
  fetchCollectionsFailure,
} from "./shop.actions";

export function* fetchCollectionsAsync() {
  yield console.log("I'm fired!");

  try {
    const collectionRef = firestore.collection("collections");
    // dispatch(fetchCollectionsStart());
    yield collectionRef.onSnapshot(async (snapshot) => {
      const collectionsMap = call(convertCollectionsSnapshotToMap, snapshot);
      put(fetchCollectionsSuccess(collectionsMap));
    });
  } catch (error) {
    put(fetchCollectionsFailure(error.message));
  }
}

export function* fetchCollectionsStart() {
  yield takeEvery(
    ShopActionTypes.FETCH_COLLECTIONS_START,
    fetchCollectionsAsync
  );
}
